import React from 'react';
import { connect } from 'react-redux';

import './MatchList.css';
import { loadDriverMatches, loadOwnerMatches, makeMatch, loadDriversInfo, loadOwnersInfo } from "./Redux/actions";
import { driver, owner } from "./types";
import ExpandableRow from '../../Components/ExpandableRow';

import MUIDataTable, { MUIDataTableOptions } from "mui-datatables";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';


type MatchListProps = {
  driverMatches: [{ id: string }],
  ownerMatches: [{ id: string, carID: string }],
  driversInfo: {[key: string]: driver},
  ownersInfo: {[key: string]: owner},
  dispatchLoadDriverMatches: any,
  dispatchLoadOwnerMatches: any,
  dispatchMakeMatch: any,
  dispatchLoadDriversInfo: any,
  dispatchLoadOwnersInfo: any
}

type MatchListState = {
  date: Date,
}

let selectedDriverIndex: number;
let selectedOwnerIndex: number;

class MatchList extends React.Component<MatchListProps, MatchListState> {

  constructor(props:MatchListProps) {
    super(props);
    this.state = {
      date: new Date(),
    }
    this.onDateChange = this.onDateChange.bind(this);
    this.onConnect = this.onConnect.bind(this);
  }

  componentDidMount() {
    this.props.dispatchLoadDriverMatches(this.state.date);
    this.props.dispatchLoadOwnerMatches(this.state.date);
  }

  onDateChange(date: Date) {
    this.setState({
      date: date,
    });
    this.props.dispatchLoadDriverMatches(date);
    this.props.dispatchLoadOwnerMatches(date);
  }

  onConnect() {
    if (selectedOwnerIndex !== undefined && selectedDriverIndex !== undefined) {
      const driverMatch = this.props.driverMatches[selectedDriverIndex];
      const ownerMatch = this.props.ownerMatches[selectedOwnerIndex];
      this.props.dispatchMakeMatch(driverMatch.id, ownerMatch.id, this.state.date);
    }
  }

  render(){
    const driverColumns = [
      {name: "status", label: "Status"},
      {name: "pickStart", label: "Pickup Start"},
      {name: "pickEnd", label: "Pickup End"},
      {name: "dropStart", label: "Dropoff Start"},
      {name: "dropEnd", label: "Dropoff End"},
      {name: "driverID", label: "Driver ID"},
    ];

    const ownerColumns = [
      {name: "status", label: "Status"},
      {name: "pickupTime", label: "Pickup Time"},
      {name: "dropoffTime", label: "Dropoff Time"},
      {name: "pickupLocation", label: "Pickup Spot"},
      {name: "dropoffLocation", label: "Dropoff Spot"},
      {name: "ownerID", label: "Owner ID"},
    ];

    const driverTableOptions: MUIDataTableOptions = {
      filterType: 'checkbox',
      selectableRows: 'single',
      responsive: 'scroll',
      pagination: false,
      print: false,
      expandableRows: true,
      onRowsSelect: currentRowsSelected => selectedDriverIndex = currentRowsSelected[0].index,
      renderExpandableRow: (rowData) => {
        if(this.props.driversInfo) {
          const characteristics = [
            {title: 'Name', value: this.props.driversInfo[rowData[5]].firstName},
            {title: 'Phone', value: this.props.driversInfo[rowData[5]].phoneNumber},
            {title: 'Stripe', value: this.props.driversInfo[rowData[5]].customerID ? 'Yes' : 'No'},
            {title: 'Checkr', value: this.props.driversInfo[rowData[5]].checkr === 'Cleared' ? 'Cleared' : 'No'}
          ];
          return <ExpandableRow characteristics={characteristics}/>
        }
        return null;
      }
    }
    const ownerTableOptions: MUIDataTableOptions = {
      filterType: 'checkbox',
      selectableRows: 'single',
      responsive: 'scroll',
      pagination: false,
      print: false,
      expandableRows: true,
      onRowsSelect: currentRowsSelected => selectedOwnerIndex = currentRowsSelected[0].index,
      renderExpandableRow: (rowData, rowMeta) => {
        if (this.props.driversInfo) {
          const characteristics = [
            {title: 'Name', value: this.props.ownersInfo[rowData[5]].firstName},
            {title: 'Phone', value: this.props.ownersInfo[rowData[5]].phoneNumber},
            {title: 'Stripe', value: this.props.ownersInfo[rowData[5]].accountId ? 'Yes' : 'No'},
            {title: 'Vehicle', value: this.props.ownerMatches[rowMeta.dataIndex].carID ? 'Yes' : 'No'}
          ];
          return <ExpandableRow characteristics={characteristics}/>
        }
        return null
      }

    };

    return (
      <div className="App">
      <div className="App-header">
      OXO Engine
      </div>
      <div className='App-datepicker'>
      <DatePicker
      selected={this.state.date}
      onChange={this.onDateChange}
      />
      </div>
      <div className="App-tables">
      <MUIDataTable
      title={"Drivers"}
      data={this.props.driverMatches}
      columns={driverColumns}
      options={driverTableOptions}
      />
      &nbsp;&nbsp;
      <MUIDataTable
      title={"Owners"}
      data={this.props.ownerMatches}
      columns={ownerColumns}
      options={ownerTableOptions}
      />
      </div>
      <div className="App-footer">
      <button onClick={this.onConnect} style={{backgroundColor: '#ff5252', borderColor: '#fafafa', paddingLeft: 15, paddingRight: 15, borderRadius: 10, fontSize: 'x-large', color: '#fafafa'}}>
      Match!
      </button>
      </div>
      </div>
    );
  }
}

const mapStateToProps = (state: any) => {
  return {
    driverMatches: state.matches.driverMatches,
    ownerMatches: state.matches.ownerMatches,
    driversInfo: state.matches.driversInfo,
    ownersInfo: state.matches.ownersInfo
  }
}

const mapDispatchToProps = (dispatch: any) => {
  return {
    dispatchLoadDriverMatches: (date: Date) => dispatch(loadDriverMatches(date)),
    dispatchLoadOwnerMatches: (date: Date) => dispatch(loadOwnerMatches(date)),
    dispatchMakeMatch: (driverMatchID: string, ownerMatchID: string, date: Date) => dispatch(makeMatch(driverMatchID, ownerMatchID, date)),
    dispatchLoadDriversInfo: (driverIDs: string[]) => dispatch(loadDriversInfo(driverIDs)),
    dispatchLoadOwnersInfo: (ownerIDs: string[]) => dispatch(loadOwnersInfo(ownerIDs))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(MatchList);
