import React, { useState, useEffect } from "react";
import { connect } from "react-redux";

import "./Database.css"
import { getDatabaseList, loadDatabase } from "./Redux/actions";
import MUIDataTable, { MUIDataTableOptions } from "mui-datatables";
import { useAuth0 } from "../../App/react-auth0-spa";

interface DatabaseProps {
    databaseList: any[];
    databaseEntries: any[];
    dispatchDatabaseList: any;
    dispatchLoadDatabase: any;

}

function Database(props: DatabaseProps): React.ReactElement {
    const { getTokenSilently } = useAuth0();
    const [token, setToken] = useState();

    useEffect(() => {
        const fetchDatabaseList = async () => {
            const newToken = await getTokenSilently();
            setToken(newToken);
            console.log('what is the new token: ', newToken)
            props.dispatchDatabaseList(newToken)
        };
        fetchDatabaseList();
    }, []);

    const generateList = () => {
        return props.databaseList.map((table: string) => {
            let capitalLetter = table[0].toUpperCase()
            let tableName = capitalLetter + table.slice(1)
            return (
                <h2
                    key={table}
                    className="tableName"
                    onClick={() => { getTable(table) }}
                >{tableName}</h2>
            )
        })
    }
    const standardColumns = [
        { name: "status", label: "Status" },
        { name: "id", label: "ID" },
        { name: "pickEnd", label: "Pickup End" },
        { name: "dropStart", label: "Dropoff Start" },
        { name: "dropEnd", label: "Dropoff End" },
    ];
    const dataColumns = [...standardColumns, ...props.uniqueColumns]



    const dataTableOptions: MUIDataTableOptions = {
        filterType: "checkbox",
        selectableRows: "single",
        pagination: false,
        print: false,
        expandableRows: true,
        onRowsSelect: currentRowsSelected =>
            (selectedDriverIndex = currentRowsSelected[0].index)
    };

    const getTable = async (tableName: string) => {
        let page: number = 1;
        let pageSize: number = 25;
        props.dispatchLoadDatabase(tableName, token, JSON.stringify(page), JSON.stringify(pageSize))

        return (
            <MUIDataTable
                title={tableName}
                data={props.driverMatches}
                columns={driverColumns}
                options={driverTableOptions}
            />
        )
    }

    return (
        <div
            className="container"
        >
            {generateList()}
        </div>
    )
}

const mapStateToProps = (state: any) => {
    return {
        databaseList: state.database.databaseList,
        databaseEntries: state.database.data
    }
}

const mapDispatchToProps = (dispatch: any) => {
    return {
        dispatchDatabaseList: (token: string) => dispatch(getDatabaseList(token)),
        dispatchLoadDatabase: (tableName: string) => dispatch(loadDatabase(tableName))
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Database);
